select

from carton_loan





          and b_seconded_customer


          and b_order_account


          and seconded_customer


          and order_account


          and material


          and flute_type


          and size


          and carton_loan_num


          and carton_loan_time


          and cent




          like "%"#{item.value}"%"


          =#{item.value}


          <>#{item.value}


          >#{item.value}


          >=#{item.value}


          <#{item.value}


          <=#{item.value}


          like #{item.value}"%"


          like "%"#{item.value}





    and carton_loan_time>=#{startTime}


    and carton_loan_time<=#{endTime}




  order by stock_id desc ,


      and b_seconded_customer


      and b_order_account


      and seconded_customer


      and order_account


      and material


      and flute_type


      and size


      and carton_loan_num


      and carton_loan_time




      asc


      desc




  and carton_loan_time>=#{startTime}


  and carton_loan_time<=#{endTime}

limit #{cur},#{rows}
