mapper.select:
select

from ticket





          and ticket_id


          and type


          and ticket_holder


          and ticket_collector


          and ticket_money


          and ticket_time


          and ticket_name


          and contacts


          and contacts_tel


          and ticket_remark




          like "%"#{item.value}"%"


          =#{item.value}


          <>#{item.value}


          >#{item.value}


          >=#{item.value}


          <#{item.value}


          <=#{item.value}


          like #{item.value}"%"


          like "%"#{item.value}





    and ticket_time>=#{startTime}


    and ticket_time<=#{endTime}

  and fake=0

order by

  ticket_id desc




      type


      ticket_money


      ticket_time




      asc


      desc



limit #{cur},#{rows}

